    <script>
        $(document).ready(function() {
            shareTopInput = $(".shareTop input")
            shareTopInput.click(function() {
                $(".share-wrap").css("display", "flex");
                $(".column").css("opacity", "0.2");
                $("body").css("overflow", "hidden");
            });

            $(document).mouseup(function(e) {
                var container = $(".share-wrap");

                // if the target of the click isn't the container nor a descendant of the container
                if (!container.is(e.target) && container.has(e.target).length === 0) {
                    container.hide();
                    $(".column").css("opacity", "1");
                    $("body").css("overflow", "scroll");

                }
            });

            shareTextArea = $(".share-container textarea");
            no_share = $(".share-container li.button button.no-share");
            share = $(".share-container li.button button.share");

            shareTextArea.bind('input propertychange', function() {

                no_share.hide();
                share.show();

                if (this.value.length < 1) {
                    share.hide();
                    no_share.show();
                }
            });

            showComments = $(".razni-komentari");

            showComments.click(function() {
                /*
                teTava = $(this);
                prom = $(this).find(".komentari");
                */
